mapper.select:
SELECT id, erpid, status, ttype, inv_part_number, extra_desc, (select
y.classname from materiel_smallclass y where y.id=a.prod_code_sell_ptr
) prodCodeSellPtrStr, prod_code_sell_ptr,
package_,(select z.supplier_Name from materiel_supplier z where z.id=a.supplier_ptr) supplierPtrStr,
(select o.packagename from materiel_package o where o.id=a.package_) package_Str,
smt_flag, inv_part_description_c, cust_part_name, cust_part_code,
purch_unit_ptr,
stock_unit_ptr, supplier_ptr, std_cost, stock_purch,remark,userid,createtime,prod_supper,(select
h.brandname from materiel_brand h where h.id=a.prod_supper
) prodSuppersStr FROM materiel_info a where 1=1

	AND a.INV_PART_NUMBER like '%${invPartNumber}%'


	AND a.PROD_SUPPER =#{prodSupper}


	AND a.PACKAGE_ = #{package_}


	AND a.EXTRA_DESC like '%${extraDesc}%'


	AND a.CUST_PART_CODE like '%${custPartCode}%'


	AND a.supplier_ptr=${supplierPtr}


	AND (
	
		a.inv_part_description_c LIKE
		'%${item}%'
	
	)


	AND a.prod_code_sell_ptr =#{prodCodeSellPtr}

limit #{start},#{row}

SELECT COUNT(*) FROM materiel_info a where 1=1

	AND a.INV_PART_NUMBER like '%${invPartNumber}%'


	AND a.PROD_SUPPER =#{prodSupper}


	AND a.PACKAGE_ = #{package_}


	AND a.EXTRA_DESC like '%${extraDesc}%'


	AND a.CUST_PART_CODE like '%${custPartCode}%'


	AND a.prod_code_sell_ptr =#{prodCodeSellPtr}


	AND a.supplier_ptr=${supplierPtr}


	AND (
	
		a.inv_part_description_c LIKE
		'%${item}%'
	
	)

SELECT COUNT(*) FROM materiel_info a where a.CUST_PART_CODE =
#{custPartCode}

	AND a.id != #{id}

SELECT COUNT(*) FROM materiel_info a where a.inv_part_description_c =
#{invPartDescriptionC}

	AND a.id != #{id}
