mapper.select:
SELECT t.* from (SELECT p.*,@rownum :=@rownum + 1 AS rn FROM (SELECT @rownum := 0) r, ( SELECT DISTINCT a.* FROM UAAS_ROLE a LEFT JOIN UAAS_USER_ROLE b ON a.id = b.roleid and b.removed = 0 WHERE a.removed = 0
AND a.NAME like concat(concat('%',#{name}),'%')
AND b.USERID = #{userid}
)p ORDER BY p.id ASC ) t WHERE t.rn >= #{start} and t.rn <= #{end}
